const axios = require('axios');
const uuid = require('uuid');
const { URLSearchParams } = require('url');

const SCOPES = {
  'AppOnlyCertGraphToken': 'https://graph.microsoft.com/.default',
  'AppOnlyCertSPOToken': bru.getEnvVar("RootSiteUrl") + "/.default",
};

async function ensureAccessToken(tokenName = 'AppOnlyCertGraphToken') {
    console.log('Ensuring access token', tokenName);
    let validToken = false;
    const token = bru.getEnvVar(tokenName);

    if (token) {
        console.log('checking stored token');
        try {
            var tokenObj = KJUR.jws.JWS.parse(token);
            var nbf = tokenObj.payloadObj.nbf;
            var exp = tokenObj.payloadObj.exp;
            var now = getTimeInSec();
            if (nbf <= now && now < exp) {
                validToken = true;
            } else {
                console.log("Stored access token is expired");
            }
        } catch (e) {
            console.log("Unable to parse stored access token");
        }

    } else {
        console.log("No access token found");
    }
    if (!validToken) {
        await acquireAccessToken(tokenName);
    }
}

async function acquireAccessToken(tokenName = 'AppOnlyCertGraphToken') {
    console.log("Acquiring a new access token", tokenName);
    const jwt = getRequestJwt();

    const tid = bru.getEnvVar('ConsumingTenantId');

    const body = {
      client_assertion_type: 'urn:ietf:params:oauth:client-assertion-type:jwt-bearer',
      client_assertion: jwt,
      client_id: bru.getEnvVar('ClientID'),
      scope: SCOPES[tokenName] || 'https://graph.microsoft.com/.default',
      grant_type: 'client_credentials',
    };
    const config = {
            header: {
                'Content-Type': 'application/x-www-form-urlencoded'
            }
        };

    const tokenUrl = `https://login.microsoftonline.com/${tid}/oauth2/v2.0/token`;

    const params = new URLSearchParams(body);

    const response = await axios.post(tokenUrl, params, config);

    var responseJson = response.data;
    var token = responseJson.access_token;
    bru.setEnvVar(tokenName, token);
}

function getRequestJwt() {
    const header = {
        'alg': 'RS256',
        'typ': 'JWT',
        'x5t': safeBase64EncodedThumbprint(bru.getEnvVar('CertThumbprint'))
    };

    const now = getTimeInSec();
    const tid = bru.getEnvVar('ConsumingTenantId');
    const payload = {
        'aud': `https://login.microsoftonline.com/${tid}/oauth2/v2.0/token`,
        'exp': now + 60 * 60,
        'iss': bru.getEnvVar('ClientID'),
        'jti': uuid.v4(),
        'nbf': now,
        'sub': bru.getEnvVar('ClientID'),
        'iat': now
    };

    const encryptedPk = bru.getEnvVar('CertPrivateKey');
    let decryptedPk = encryptedPk;
    if (bru.getEnvVar('CertPassword')) {
        decryptedPk = KEYUTIL.getKey(encryptedPk, bru.getEnvVar('CertPassword'));
    }
    const sHeader = JSON.stringify(header);
    const sPayload = JSON.stringify(payload);

    return KJUR.jws.JWS.sign(header.alg, sHeader, sPayload, decryptedPk);
}

function getTimeInSec() {
    return Math.floor(Date.now() / 1000);
}

function safeBase64EncodedThumbprint(thumbprint) {
    const numCharIn128BitHexString = 128 / 8 * 2;
    const numCharIn160BitHexString = 160 / 8 * 2;
    const thumbprintSizes = {};
    thumbprintSizes[numCharIn128BitHexString] = true;
    thumbprintSizes[numCharIn160BitHexString] = true;
    const thumbprintRegExp = /^[a-f\d]*$/;

    const hexString = thumbprint.toLowerCase().replace(/:/g, '').replace(/ /g, '');

    if (!thumbprintSizes[hexString.length] || !thumbprintRegExp.test(hexString)) {
        throw 'The thumbprint does not match a known format';
    }

    const base64 = (Buffer.from(hexString, 'hex')).toString('base64');
    return base64.replace(/\+/g, '-').replace(/\//g, '_').replace(/=/g, '');
}

globalThis.navigator = {};
globalThis.window = {};

if (bru.getEnvVar('jsrsasign-js')) {
    // eval actually parses the library 
    let jsRsa = decodeURI(bru.getEnvVar('jsrsasign-js'))
    eval(jsRsa);
    await ensureAccessToken();
} else {
  console.log('downloading RSA library');
  const axios = require('axios');
  const response = await axios.get(
    'https://kjur.github.io/jsrsasign/jsrsasign-all-min.js',
  );
  ///console.log(response.data);
  console.log('Downloaded RSA library');
  // Why encode and decode the RSA library?
  // 1. Environments can't be loaded if they have new lines.
  // 2. Bruno doesn't detect changes made to env vars through a script.
  // 3. Therefore, changes made to env vars through a script are not saved.
  // 4. The RSA library has new lines
  // 5. if for some reason a user makes changes to the environment
  //    then all current environment vars are saved, including the RSA libray,
  //    which makes the environment file "not loadable"
  // 6. Encoding converts the data into a single (very large) line
  // 7. Problem solved  
  let encodedJsRsa = encodeURI(response.data);
  bru.setEnvVar('jsrsasign-js', encodedJsRsa);
}

